rule.definition:
load(
  local!action: null,
  local!seconds,
  local!reset,
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: "Stopwatch",
    contents: {
      stopWatchField(
        labelPosition: "ABOVE",
        height: "AUTO",
        action: local!action,
        size: "LARGE",
        align: "CENTER",
        color: "POSITIVE",
        style: null,
        timerDirection: "UP",
        enableReverseTimer: null,
        reverseTimerColor: "NEGATIVE",
        secondsValue: if(
          local!reset,
          0,
          0 + tointeger(
            local!seconds
          )
        ),
        secondsSaveInto: local!seconds
      ),

    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: " Start",
          value: "START",
          saveInto: {
            local!action,
            a!save(
              local!reset,
              false()
            )
          },
          disabled: local!action = "START",
          style: "PRIMARY"
        ),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Stop",
          value: "STOP",
          saveInto: {
            local!action,
            a!save(
              local!reset,
              false()
            )
          },
          disabled: local!action = "STOP",
          style: "PRIMARY"
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "RESET & START",
          value: true(),
          disabled: local!action = "START",
          saveInto: {
            local!reset,
            a!save(
              local!action,
              "START"
            )
          },
          style: "PRIMARY"
        )
      }
    )
  )
)
