rule.definition:
a!localVariables(
  local!creating: a!refreshVariable(
    value: isnull(ri!record),
    refreshOnReferencedVarChange: false
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: if(
      local!creating,
      "Add New Leave Request",
      "Update Leave Request"
    ),
    contents: {
      #"_a-0000e6a2-9a44-8000-65da-01ef9001ef90_186578"(
        record: ri!record,
        creating: local!creating,
        readOnly: false
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        if(
          local!creating,
          #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
            label: "Submit Leave Request",
            style: "PRIMARY",
            saveInto: {
              a!save(ri!record.createdOn, now()),
              a!save(ri!record.createdBy, loggedInUser()),
              a!save(ri!record.updatedOn, now()),
              a!save(ri!record.updatedBy, loggedInUser())
            }
          ),
          #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
            label: "Update Leave Request",
            style: "PRIMARY",
            saveInto: {
              a!save(ri!record.updatedOn, now()),
              a!save(ri!record.updatedBy, loggedInUser())
            }
          )
        )
      },
      secondaryButtons: {
        if(
          local!creating,
          #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
            label: "Cancel",
            style: "NORMAL",
            value: true,
            saveInto: {
              ri!cancel
            },
          skipValidation: true
          ),
          #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
            label: "Cancel",
            style: "NORMAL",
            value: true,
            saveInto: {
              ri!cancel
            },
            skipValidation: true
          )
        )
      }
    )
  )
)
