rule.definition:
a!localvariables(
  local!seconds,
  local!action: null,
  local!reset,
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: "Stopwatch",
    contents: {
      {
        stopWatchField(
          label: "Stopwatch Timer Demonstrating Automatic Stop at 45 Seconds in a  minute",
          labelPosition: "ABOVE",
          height: "AUTO",
          action: local!action,
          size: "MEDIUM",
          align: "CENTER",
          expiredMessage: null,
          color: "POSITIVE",
          style: null,
          timerDirection: null,
          enableReverseTimer: null,
          reverseTimerColor: null,
          secondsValue: if(
            local!reset,
            60,
            tointeger(local!seconds)
          ),
          secondsSaveInto: {a!save(local!action,"STOP"),local!seconds},
          enableAutoStop: true,
          autoStopTimerSeconds: 45
        ),

      }
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: " Start",
          value: "START",
          saveInto: {
            local!action,
            a!save(local!reset, true())
          },
          disabled: local!action = "START",
          style: "PRIMARY"
        ),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Stop",
          value: "STOP",
          saveInto: {
            local!action,
            a!save(local!reset, false())
          },
          disabled: local!action = "STOP",
          style: "PRIMARY"
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "RESET & START",
          value: true(),
          disabled: local!action = "START",
          saveInto: {
            local!reset,
            a!save(local!action, "START")
          },
          style: "PRIMARY"
        )
      }
    )
  )
)
